CREATE PROCEDURE [dbo].[spCreateDepartment]
	@Name varchar(256),
	@Description nvarchar(MAX),
	@CompanyId int
AS

	INSERT INTO [Employee] 
				(
					FirstName,
					LastName,
					Birthdate,
					DepartmentId,
					AdressId
				) 
	VALUES 
				(
					@FirstName,
